import React, { useState } from 'react';
import axios from 'axios';
import './Formulario.css';  // Importando o arquivo CSS

function App() {
  const [form, setForm] = useState({
    dia: '',
    mes: '',
    ano: '',
    empresa_nome: '',
    empresa_endereco: '',
    empresa_telefone: '',
    nome: '',
    email: '',
    curso: '',
    periodo: '',
    convalidacao_inicio: '',
    convalidacao_fim: '',
    empresa_supervisor: '',
    atividades_desenvolvidas: '',
  });

  const handleChange = (e) => {
    const { name, value } = e.target;

    if (name === "convalidacao_inicio" || name === "convalidacao_fim") {
      setForm({ ...form, [name]: formatarDataParaState(value) });
    } else if (name === "data") {
      const data = value.split("-");
      setForm({
        ...form,
        dia: data[2],
        mes: getNomeMes(data[1]),
        ano: data[0]
      });
    } else {
      setForm({ ...form, [name]: value });
    }
  };

  // const handleData = (e) => {
  //   const data = e.target.value.split("-");
  //   setForm({
  //     ...form,
  //     dia: data[2],
  //     mes: getNomeMes(data[1]),
  //     ano: data[0]
  //   });
  // };

  function formatarDataParaState(data) {
    if (!data) return "";
    const [ano, mes, dia] = data.split("-");
    return `${dia}/${mes}/${ano}`; // Converte para dd/mm/aaaa
  }

  function getDataParaInput(data) {
    if (!data) return "";
    const [dia, mes, ano] = data.split("/");
    return `${ano}-${mes}-${dia}`; // Converte para aaaa-mm-dd
  }

  function getNomeMes(mes) {
    const meses = [
      'Janeiro', 'Fevereiro', 'Março', 'Abril', 'Maio', 'Junho',
      'Julho', 'Agosto', 'Setembro', 'Outubro', 'Novembro', 'Dezembro'
    ];
    return meses[parseInt(mes, 10) - 1] || 'Mês inválido';
  }

  const handleSubmit = async (e) => {
    e.preventDefault();

    const url = 'http://localhost:3001/TCC1_declaracao_supervisor_atividades-pdf';

    try {
      const response = await axios.post(url, form, {
        responseType: 'blob'
      });

      const downloadUrl = window.URL.createObjectURL(new Blob([response.data]));
      const link = document.createElement('a');
      link.href = downloadUrl;
      link.setAttribute('download', `04_Declaracao Supervisor - ${form.nome}.pdf`);
      document.body.appendChild(link);
      link.click();
      link.remove();
    } catch (error) {
      console.error('Erro ao gerar o PDF', error);
    }
  };

  return (
    <div className="form-container">
      <div className='header'>
        <div>
          <img src="utfpr.svg" width="180px" alt="UTFPR logo" />
        </div>
        <div className='header-title'>
          <span>Universidade Tecnológica Federal do Paraná</span><br />
          <span>Câmpus Cornélio Procópio</span><br />
          <span>Departamento de Computação</span>
        </div>
      </div>
      <hr />
      <h2>TCC1 - Declaração do Supervisor/Cliente</h2>
      <form onSubmit={handleSubmit} className="form">
        <div className="form-group">
          <label className="form-label">Data de vínculo:</label>
          <input
            type="date"
            name="data"
            value={form.data}
            onChange={handleChange}
            required
            className="form-input"
          />
        </div>
        <div className="form-group">
          <label className="form-label">Nome da empresa:</label>
          <input
            type="text"
            name="empresa_nome"
            value={form.empresa_nome}
            onChange={handleChange}
            required
            className="form-input"
          />
        </div>
        <div className="form-group">
          <label className="form-label">Endereço da empresa:</label>
          <textarea
            type="text"
            name="empresa_endereco"
            value={form.empresa_endereco}
            onChange={handleChange}
            required
            className="form-input"
          />
        </div>
        <div className="form-group">
          <label className="form-label">Telefone da empresa:</label>
          <input
            type="tel"
            name="empresa_telefone"
            value={form.empresa_telefone}
            onChange={handleChange}
            required
            className="form-input"
          />
        </div>
        <div className="form-group">
          <label className="form-label">Nome do supervisor da empresa:</label>
          <input
            name="empresa_supervisor"
            value={form.empresa_supervisor}
            onChange={handleChange}
            required
            className="form-input"
          />
        </div>
        <div className="form-group">
          <label className="form-label">Nome do estudante:</label>
          <input
            type="text"
            name="nome"
            value={form.nome}
            onChange={handleChange}
            required
            className="form-input"
          />
        </div>
        <div className="form-group">
          <label className="form-label">E-mail:</label>
          <input
            type="email"
            name="email"
            value={form.email}
            onChange={handleChange}
            required
            className="form-input"
          />
        </div>
        <div className="form-group">
          <label className="form-label">Curso:</label>
          <input
            name="curso"
            value={form.curso}
            onChange={handleChange}
            required
            className="form-input"
          />
        </div>
        <div className="form-group">
          <label className="form-label">Período no curso:</label>
          <input
            name="periodo"
            value={form.periodo}
            onChange={handleChange}
            required
            className="form-input"
          />
        </div>
        <div className="form-group">
          <label className="form-label">Data de início do trabalho/estágio:</label>
          <input
            name="convalidacao_inicio"
            type="date"
            value={getDataParaInput(form.convalidacao_inicio)}
            onChange={handleChange}
            required
            className="form-input"
          />
        </div>
        <div className="form-group">
          <label className="form-label">Data do fim do trabalho/estágio:</label>
          <input
            name="convalidacao_fim"
            type="date"
            value={getDataParaInput(form.convalidacao_fim)}
            onChange={handleChange}
            required
            className="form-input"
          />
        </div>
        <div className="form-group">
          <label className="form-label">Atividades desenvolvidas na empresa:</label>
          <textarea
            name="atividades_desenvolvidas"
            value={form.atividades_desenvolvidas}
            onChange={handleChange}
            required
            className="form-input"
          />
        </div>

        <button type="submit" className="form-button">
          <span className="button-text">Gerar PDF</span>
        </button>
      </form>
    </div>
  );
}

export default App;
