import React, { useState } from 'react';
import axios from 'axios';
import './Formulario.css';  // Importando o arquivo CSS

function App() {
  const [form, setForm] = useState({
    nome: '',
    email: '',
    mensagem: '',
    dia: '',
    mes: '',
    ano: '',

  });

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleData = (e) => {

    const data = e.target.value.split("-")
    console.log(data)
    console.log(form)
    setForm({ ...form, dia: data[2], mes: data[1], ano: data[0] });


  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.post('http://localhost:3001/TCC_01_Ficha_de_Vinculo-pdf', form, {
        responseType: 'blob'
      });

      const url = window.URL.createObjectURL(new Blob([response.data]));
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', `01_Ficha_de_Vinculo - ${form.nome}.pdf`);
      document.body.appendChild(link);
      link.click();
      link.remove();
    } catch (error) {
      console.error('Erro ao gerar o PDF', error);
    }
  };

  return (
    <div className="form-container">
      <h2>TCC1 - Ficha de Vínculo</h2>
      <form onSubmit={handleSubmit} className="form">
        <div className="form-group">
          <label className="form-label">Data de vínculo:</label>
          <input
            type="date"
            name="data"
            value={form.data}
            onChange={handleData}
            required
            className="form-input"
          />
        </div>
        <div className="form-group">
          <label className="form-label">Nome completo:</label>
          <input
            type="text"
            name="nome"
            value={form.nome}
            onChange={handleChange}
            required
            className="form-input"
          />
        </div>

        <div className="form-group">
          <label className="form-label">E-mail:</label>
          <input
            type="email"
            name="email"
            value={form.email}
            onChange={handleChange}
            required
            className="form-input"
          />
        </div>

        <div className="form-group">
          <label className="form-label">Endereço:</label>
          <textarea
            name="Endereço"
            value={form.endereco}
            onChange={handleChange}
            required
            className="form-textarea"
          />
        </div>

        <button type="submit" className="form-button">
          Gerar PDF
        </button>
      </form>
    </div>
  );
}

export default App;
